<script setup>

const isActive = ref(false);
const heroSvg = ref(null);

function toggleBoolean(value) {
    isActive.value = value;
}

function updateMousePosition(event) {
    if (heroSvg.value) {
        const svgRect = heroSvg.value.getBoundingClientRect();
        const mouseX = event.clientX - svgRect.left;
        const mouseY = event.clientY - svgRect.top;
        const spotlight = heroSvg.value.querySelector('#spotlight');
        if (spotlight) {
            spotlight.setAttribute('cx', mouseX);
            spotlight.setAttribute('cy', mouseY);
        }
    }
}

onMounted(() => {
    window.addEventListener('mousemove', updateMousePosition);
});

onUnmounted(() => {
    window.removeEventListener('mousemove', updateMousePosition);
});
</script>


